package inheritance;

public class HerirachalInheritanceMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		    Dog dog = new Dog();
	        dog.eat(); // Accessing eat() method from the Animal class
	        dog.bark(); // Accessing bark() method from the Dog class

	        Cat cat = new Cat();
	        cat.eat(); // Accessing eat() method from the Animal class
	        cat.meow(); // Accessing meow() method from the Cat class
	}

}
